sys_variable_value.value:
var strLog = '';
function Log(strText)
{
	strLog = strLog + "\n" + strText;
}
(function(outputs, steps, stepResult, assertEqual)
{
	var RenderValue = x_snc_devtools.RenderValue;

	var bResult = true;
	

	function TestCase_Clone(vInput, vInput2, vExpected)
	{
		Log('TestCase_Clone');
		Log('Input            : '+RenderValue(vInput));
		Log('GetType(Input)   : '+GetType(vInput));
		Log('IsObject(Input)  : '+RenderValue(IsObject(vInput)));
		Log('Expected         : '+RenderValue(vExpected));
		Log('GetType(Expected): '+GetType(vExpected));
		var vResult = Clone(vInput);
		Log('Result           : '+RenderValue(vResult));
		Log('GetType(Result)  : '+GetType(vResult));
		vInput = vInput2;
		Log('Input after mod. : '+RenderValue(vInput));
		Log('GetType(Input)   : '+GetType(vInput));

		Log('Result after mod.: '+RenderValue(vResult));
		Log('GetType(Result)  : '+GetType(vResult));
		
		if (IsEqual(vResult,vExpected) == false)
		{
			bResult = false;
			Log('IsEqual() returned false');
			Log('Test case FAILED');
		}
		if (GetType(vResult) != GetType(vExpected))
		{
			bResult = false;
			Log('GetType() differs');
			Log('Test case FAILED');
		}
		Log('');
	}
	
	TestCase_Clone('John','Jane','John');
	TestCase_Clone(42,23,42);
	TestCase_Clone(['Alpha','Beta'],['Gamma','Epsilon'],['Alpha','Beta']);
	
	TestCase_Clone(
		{a:1,b:2,c:3},
		{a:10,b:20,c:30},
		{a:1,b:2,c:3});
		
	stepResult.setOutputMessage(strLog);
	return bResult;
	
})(outputs, steps, stepResult, assertEqual);
