sys_variable_value.value:
var strLog = '';
function Log(strText)
{
	strLog = strLog + "\n" + strText;
}
(function(outputs, steps, stepResult, assertEqual)
{
	var RenderValue = x_snc_devtools.RenderValue;

	var bResult = true;
	
	function TestCase_RenderBytes(vInput, vExpected)
	{
		Log('TestCase_RenderBytes');
		Log('Input: '+RenderValue(vInput));
		Log('Expected: '+RenderValue(vExpected));
		var vResult = RenderBytes(vInput);
		Log('Result  : '+RenderValue(vResult));
		if (vResult != vExpected)
		{
			bResult = false;
			Log('Test case FAILED');
		}
		Log('');
	}

	TestCase_RenderBytes(null,'');
	TestCase_RenderBytes('','');
	TestCase_RenderBytes(-1,'');
	TestCase_RenderBytes(0,'0 Bytes');
	TestCase_RenderBytes(1,'1 Byte');
	TestCase_RenderBytes(2,'2 Bytes');
	TestCase_RenderBytes(500,'500 Bytes');
	TestCase_RenderBytes(999,'999 Bytes');
	TestCase_RenderBytes(1023,'1023 Bytes');
	TestCase_RenderBytes(1024,'1 KB');
	TestCase_RenderBytes(1024*2,'2 KB');
	TestCase_RenderBytes(1024*511,'511 KB');
	TestCase_RenderBytes(1024*512,'512 KB');
	TestCase_RenderBytes(1024*1024,'1.0 MB');
	TestCase_RenderBytes(1024*1024+1024,'1.0 MB');
	TestCase_RenderBytes(1024*1024+1024*100,'1.1 MB');
	TestCase_RenderBytes(1024*1024*512+1024*100,'512.1 MB');
	TestCase_RenderBytes(1024*1024*1023,'1023.0 MB');
	TestCase_RenderBytes(1024*1024*1023+1024*100,'1023.1 MB');
	TestCase_RenderBytes(1024*1024*1024,'1.00 GB');
	TestCase_RenderBytes(1024*1024*1024+1024*1024*10,'1.01 GB');

	
	stepResult.setOutputMessage(strLog);
	return bResult;
	
})(outputs, steps, stepResult, assertEqual);
