sys_variable_value.value:
var strLog = '';
function Log(strText)
{
	strLog = strLog + "\n" + strText;
}
(function(outputs, steps, stepResult, assertEqual)
{
	var RenderValue = x_snc_devtools.RenderValue;
	var GetType = x_snc_devtools.GetType;
	
	var testdata = new TestDataAPI('_ATF_DEVTOOLS_');

	var bResult = true;
		
	function TestCase_RecordBulkProcessor(
		grRecords,
		callbackRecordProcessor,
		strFieldDateTime,
		nMaxRecords,
		nMaxSeconds,
		callbackLog,
		nLogFrequency,
		vExpected)
	{
		Log('TestCase_RecordBulkProcessor');
		var strRecordClassName = '';
		if (IsValidRecord(grRecords) == true)
		{
			strRecordClassName = grRecords.getRecordClassName();
		}
		Log('Class name              : '+strRecordClassName);
		Log('callbackRecordProcessor : '+GetType(callbackRecordProcessor));
		Log('strFieldDateTime        : '+RenderValue(strFieldDateTime));
		Log('nMaxRecords             : '+RenderValue(nMaxRecords));
		Log('nMaxSeconds             : '+RenderValue(nMaxSeconds));
		Log('callbackLog             : '+GetType(callbackLog));
		Log('nLogFrequency           : '+RenderValue(nLogFrequency));
		Log('Expected                : '+RenderValue(vExpected));
		var vResult = RecordBulkProcessor(
			grRecords,
			callbackRecordProcessor,
			strFieldDateTime,
			nMaxRecords,
			nMaxSeconds,
			callbackLog,
			nLogFrequency);
		Log('Result  : '+RenderValue(vResult));
		if (vResult != vExpected)
		{
			bResult = false;
			Log('Test case FAILED');
		}
		Log('');
	}
	
	// Prepare test data
	for (var nIndex = 0; nIndex < 10; nIndex++)
	{
		testdata.CreateTestItem('TEST1_'+nIndex,'VALUE-'+nIndex);
	}
	
	// RecordProcessor
	function CallbackRecordProcessor(grRecord)
	{
		var strValue = GetStringValue(grRecord.getValue('value'));
		if (strValue != strValue.toLowerCase())
		{
			grRecord.value = strValue.toLowerCase();
			return true;
		}
		return false;
	}
	
	// Log function
	function CallbackLog(strText)
	{
		Log('Testing RecordBulkProcessor: '+strText);
	}
	
	Log(RenderValue(testdata.GetAllTestItems()));
	
	// Test 1: out of 10, process 2
	var grRecords = new GlideRecord('x_snc_devtools_test');
	grRecords.addQuery('name','STARTSWITH',testdata.GetPrefix());
	TestCase_RecordBulkProcessor(
		grRecords,
		CallbackRecordProcessor,
		'processed',
		2, 
		10,
		CallbackLog,
		1,
		2);
	
	Log(RenderValue(testdata.GetAllTestItems()));
	
	Sleep(2);
	
	// Test 2: out of the same 10, process 5 more, so we should have changed 7 in total
	var grRecords = new GlideRecord('x_snc_devtools_test');
	grRecords.addQuery('name','STARTSWITH',testdata.GetPrefix());
	TestCase_RecordBulkProcessor(
		grRecords,
		CallbackRecordProcessor,
		'processed',
		5,
		10,
		CallbackLog,
		1,
		5);	

	Log(RenderValue(testdata.GetAllTestItems()));
	
	Sleep(2);
	
	// Test 3: out of the same 10, process 5 more, which means that all 10 have been processed once and 2 for a second time.
	var grRecords = new GlideRecord('x_snc_devtools_test');
	grRecords.addQuery('name','STARTSWITH',testdata.GetPrefix());
	TestCase_RecordBulkProcessor(
		grRecords,
		CallbackRecordProcessor,
		'processed',
		5,
		10,
		CallbackLog,
		1,
		3);	
	
	Log(RenderValue(testdata.GetAllTestItems()));
	
	Sleep(2);
	
	// Test 4: Process 5 more - no changes expected
	var grRecords = new GlideRecord('x_snc_devtools_test');
	grRecords.addQuery('name','STARTSWITH',testdata.GetPrefix());
	TestCase_RecordBulkProcessor(
		grRecords,
		CallbackRecordProcessor,
		'processed',
		5,
		10,
		CallbackLog,
		1,
		0);		
	
	Log(RenderValue(testdata.GetAllTestItems()));
	
	Sleep(2);
	
	stepResult.setOutputMessage(strLog);
	return bResult;
	
})(outputs, steps, stepResult, assertEqual);
