sys_variable_value.value:
var strLog = '';
function Log(strText)
{
	strLog = strLog + "\n" + strText;
}
(function(outputs, steps, stepResult, assertEqual)
{
	var RenderValue = x_snc_devtools.RenderValue;

	var bResult = true;
	
	function TestCase_StopWatch()
	{
		function LogStopWatch(strTitle, stopwatch)
		{
			Log('\n'+strTitle+RenderValue(stopwatch));
			Log('stopwatch.GetMilliSeconds(): '+stopwatch.GetMilliSeconds());
			Log('stopwatch.GetSeconds(): '+stopwatch.GetSeconds());
			Log('stopwatch.GetTotalMilliSeconds(): '+stopwatch.GetTotalMilliSeconds());
			Log('stopwatch.GetTotalSeconds(): '+stopwatch.GetTotalSeconds());
		}

		Log('TestCase_StopWatch');
		
		var stopwatch = new StopWatch();
		LogStopWatch('',stopwatch);
		
		stopwatch.Reset();
		LogStopWatch('stopwatch.Reset()',stopwatch);
				
		stopwatch.Start();
		LogStopWatch('stopwatch.Start()',stopwatch);
		
		Sleep(1);
		LogStopWatch('Sleep(1)',stopwatch);
		
		stopwatch.Stop();
		LogStopWatch('stopwatch.Stop()',stopwatch);
		
		Sleep(1);
		LogStopWatch('Sleep(1)',stopwatch);
		
		stopwatch.Start();
		LogStopWatch('stopwatch.Start()',stopwatch);
		
		Sleep(1);
		LogStopWatch('Sleep(1)',stopwatch);
		
		stopwatch.Stop();
		LogStopWatch('stopwatch.Stop()',stopwatch);
		
		stopwatch.Reset();
		LogStopWatch('stopwatch.Reset()',stopwatch);
		
		Log('');
	}
	
	TestCase_StopWatch();
		
	stepResult.setOutputMessage(strLog);
	return bResult;
	
})(outputs, steps, stepResult, assertEqual);
