sys_variable_value.value:
var strLog = '';
function Log(strText)
{
	strLog = strLog + "\n" + strText;
}
(function(outputs, steps, stepResult, assertEqual)
{
	var RenderValue = x_snc_devtools.RenderValue;

	var bResult = true;
	
	function TestCase_RecordSetValue(strInputTable, strInputSysId, strInputField, vInputValue, bExpected)
	{
		Log('TestCase_RecordSetValue');
		Log('Input Table: '+RenderValue(strInputTable));
		Log('Input SysId: '+RenderValue(strInputSysId));
		Log('Input Field: '+RenderValue(strInputField));
		Log('Input Value: '+RenderValue(vInputValue));
		Log('Expected   : '+RenderValue(bExpected));
		var vResult = RecordSetValue(strInputTable, strInputSysId, strInputField, vInputValue);
		Log('Result  : '+RenderValue(vResult));
		
		if (IsEqual(vResult,bExpected) == false)
		{
			bResult = false;
			Log('Test case FAILED: Unexpected return value.');
		}
		else
		{
			if (bExpected == true)
			{
				var grRecord = new GlideRecord(strInputTable);
				if (grRecord.get(strInputSysId) == false)
				{
					Log('Test case UNDEFINED: Record could not be loaded.');
				}
				else
				{
					var vResultValue = grRecord.getValue(strInputField)
					if (IsEqual(vResultValue,vInputValue) == false)
					{
						bResult = false;
						Log('Test case FAILED: Value has not been set.');
					}
				}
			}
		}
		Log('');
	}
	
	var grTest = new GlideRecord('x_snc_devtools_test');
	grTest.initialize();
	grTest.value = 'Hello Europe';
	strSysId = grTest.insert();
	TestCase_RecordSetValue('x_snc_devtools_test',strSysId,'value','Hello World',true);
	
	TestCase_RecordSetValue('thistabledoesnotexist','invalidsys_id','value','Hello World',false);
	TestCase_RecordSetValue('x_snc_devtools_test','invalidsysid','value','Hello World',false);
	TestCase_RecordSetValue('x_snc_devtools_test',strSysId,'thisvaluedoesnotexist','Hello World',false);
	
	stepResult.setOutputMessage(strLog);
	return bResult;
	
})(outputs, steps, stepResult, assertEqual);
