sys_variable_value.value:
var strLog = '';
function Log(strText)
{
	strLog = strLog + "\n" + strText;
}
(function(outputs, steps, stepResult, assertEqual)
{
	var RenderValue = x_snc_devtools.RenderValue;

	var bResult = true;
	
	function TestCase_IsObject(vInput, vExpected)
	{
		Log('TestCase_IsObject');
		Log('Input: '+RenderValue(vInput));
		Log('GetType: '+RenderValue(GetType(vInput)));
		Log('Expected: '+RenderValue(vExpected));
		var vResult = IsObject(vInput);
		Log('Result  : '+RenderValue(vResult));
		if (vResult != vExpected)
		{
			bResult = false;
			Log('Test case FAILED');
		}
		Log('');
	}
	
	
	TestCase_IsObject(undefined,false);
	TestCase_IsObject(null,false);
	TestCase_IsObject(false,false);
	TestCase_IsObject(true,false);
	TestCase_IsObject(42,false);
	TestCase_IsObject('Hello World',false);
	TestCase_IsObject([],false);
	
	TestCase_IsObject({},true);
	TestCase_IsObject(new String(),true);
	TestCase_IsObject(new Date(),true);
	//TestCase_IsObject(new GlideDateTime(),true);
	TestCase_IsObject(new GlideRecord('incident'),true);

	var grIncident = new GlideRecord('incident');
	grIncident.initialize();
	grIncident.short_description = 'Hello World';
	TestCase_IsObject(grIncident.short_description,true);
	
		
	stepResult.setOutputMessage(strLog);
	return bResult;
	
})(outputs, steps, stepResult, assertEqual);
